                    new Product { Name = "Vestido Ariel - Marino", Description = "Cuando el sol sale nos apetecen los estampados alegres y a todo color. Por eso hemos diseñado esta versión del vestido Ariel que es puro verano. Su patrón ya lo conoces, es nuestro top ventas máximo porque es perfecto para todas. Cruzado, con escote de pico y manguita corta. Ideal. Ya sabes, llévalo a la oficina, de compras o una cena de verano. La modelo mide 1,62m y lleva la talla 34.", Price = 36, Stock = 15, IsDeleted = false, IsNew = true, Categoria = Categorias["Vestidos"], ImageUrl = "/images/v1.webp" },
                    new Product { Name = "Vestido Bea - Crudo", Description = "El vestido más buscado de la temporada. Largo y de punto calado con apariencia croché. Perfectísimo para pasear junto al mar después de una jornada de playa, o para sentarte en una terraza con tus mejillas sonrosadas mientras brindas por la vida. Un vestido cómodo y con rollazo de edición limitada. Lleva vestidito de forro interior. La modelo de la talla 36/38 mide 1,62 m y la modelo de la talla 44/46 mide 1,58 m. 49%Algodón 49%Poliéster 2%Elastán", Price = 45, Stock = 10, IsDeleted = false, IsNew = true, Categoria = Categorias["Vestidos"], ImageUrl = "/images/v2.webp" },
                    new Product { Name = "Vestido Ariel - Fucsia", Description = "Cuando el sol sale nos apetecen los estampados alegres y a todo color. Por eso hemos diseñado esta versión del vestido Ariel que es puro verano. Su patrón ya lo conoces, es nuestro top ventas máximo porque es perfecto para todas. Cruzado, con escote de pico y manguita corta. Ideal. Ya sabes, llévalo a la oficina, de compras o una cena de verano.", Price = 37, Stock = 12, IsDeleted = false, IsNew = true, Categoria = Categorias["Vestidos"], ImageUrl = "/images/v3.webp" },
