                    new Product { Name = "Vestido Alka - Negro", Description = "Hay vestidos y VESTIDOS. Ideal para una vichy addict como nosotras, este vestido es el look de la primavera. Nos encanta con complementos rojos, verdes, fucsias o amarillos. De corte recto y manguita estilo volante, lleva un botón en la espalda. ¡Qué ganas de estrenarlo! La modelo de la talla 36 mide 1,7 m y la modelo de la talla 44 mide 1,58 m.100%Viscosa", Price = 55, Stock = 10, IsDeleted = false, IsNew = true, Categoria = Categorias["Vestidos"], ImageUrl = "~/images/v5.webp" },
                    new Product { Name = "Falda Pantalón Allace - Mostaza", Description = "Dale estilo y color a tus looks con esta falda pantalón. Su tejido es una sarguita fluida elástica, ideal. Lleva cremallera lateral. La modelo de la talla 36 mide 1,57 m. 95%Poliéster 5%Elastán", Price = 55, Stock = 10, IsDeleted = false, IsNew = true, Categoria = Categorias["Faldas"], ImageUrl = "/images/f1.webp" },
